example.code:
//
// Lambda style
//

// listening
notification = AVAudioSession.Notifications.ObserveObserveInterruption ((sender, args) => {
    /* Access strongly typed args */
    Console.WriteLine ("Notification: {0}", args.Notification);

    Console.WriteLine ("InterruptionType", args.InterruptionType);
    Console.WriteLine ("Option", args.Option);
});

// To stop listening:
notification.Dispose ();

//
//Method style
//
NSObject notification;
void Callback (object sender, ObserveInterruption args)
{
    // Access strongly typed args
    Console.WriteLine ("Notification: {0}", args.Notification);

    Console.WriteLine ("InterruptionType", args.InterruptionType);
    Console.WriteLine ("Option", args.Option);
}

void Setup ()
{
    notification = AVAudioSession.Notifications.ObserveObserveInterruption (Callback);
}

void Teardown ()
{
    notification.Dispose ();
}

