example.code:
//
// Lambda style
//

// listening
notification = UIApplication.Notifications.ObserveObserveDidBecomeActive ((sender, args) => {
    /* Access strongly typed args */
    Console.WriteLine ("Notification: {0}", args.Notification);
});

// To stop listening:
notification.Dispose ();

//
//Method style
//
NSObject notification;
void Callback (object sender, ObserveDidBecomeActive args)
{
    // Access strongly typed args
    Console.WriteLine ("Notification: {0}", args.Notification);
}

void Setup ()
{
    notification = UIApplication.Notifications.ObserveObserveDidBecomeActive (Callback);
}

void Teardown ()
{
    notification.Dispose ();
}

