example.code:
//
// Lambda style
//

// listening
notification = UIDevice.Notifications.ObserveObserveBatteryLevelDidChange ((sender, args) => {
    /* Access strongly typed args */
    Console.WriteLine ("Notification: {0}", args.Notification);
});

// To stop listening:
notification.Dispose ();

//
//Method style
//
NSObject notification;
void Callback (object sender, ObserveBatteryLevelDidChange args)
{
    // Access strongly typed args
    Console.WriteLine ("Notification: {0}", args.Notification);
}

void Setup ()
{
    notification = UIDevice.Notifications.ObserveObserveBatteryLevelDidChange (Callback);
}

void Teardown ()
{
    notification.Dispose ();
}

